import React, {useState, useEffect} from 'react';
import {Link} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [pirate, setPirate]= useState({});

    useEffect(() =>{
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setPirate(res.data);
            })
    },[])

    return (
        <div>
            <h2 className="App-navbar">{pirate.name}</h2>
            <p>Type: {pirate.type}</p>
            <p>Description: {pirate.description}</p>
            <p>Are they King?: {pirate.isKing ? "yes" : "no"}</p>
            <Link to="/">
                <button>Back</button>
            </Link>
            <Link to={"/pirates/"+pirate._id+"/edit"}>
                <button>Edit Pirate</button>
            </Link>
        </div>
    )
}

export default ShowOne;
