import React, {useState, useEffect} from 'react';
import {Link} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [pirate, setPirate]= useState({});

    useEffect(() =>{
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setPirate(res.data);
            })
    },[])

    const toggleFeature = (a) => {
        if (a==true){
            a = false;
        }else if (a == false){
            a = true;
        }
    }

    return (
        <div className="wrapper" style={{padding: "0px"}}>
            <h2 className="App-navbar">{pirate.name}</h2>
            <div>
                <div className="fiftyFiftySplit">
                    <img src={pirate.url} alt="Pirate Image" width="200" height="100" style={{margin:"10px"}}/>
                    <h2>"{pirate.catchPhrase}"</h2>
                </div>


                <div className="fiftyFiftySplit" style={{backgroundColor: "white"}}>
                    <h3>About</h3>
                    <p>Position: {pirate.position}</p>
                    <p>Treasures: {pirate.treasure}</p>
                    <div>
                        <div style={{display: "inline-block"}}>
                            <p>Peg Leg: {pirate.hasPegLeg ? "yes" : "no"}</p>
                            <p>Eye Patch: {pirate.hasEyePatch ? "yes" : "no"}</p>
                            <p>Hook Hand: {pirate.hasHookHand ? "yes" : "no"}</p>
                        </div>
                        <div style = {{display: "inline-block"}}>
                            <button style={{display: "block", margin: "16px 20px"}}>{pirate.hasPegLeg ? "yes" : "no"}</button>
                            <button style={{display: "block", margin: "16px 20px"}}>{pirate.hasEyePatch ? "yes" : "no"}</button>
                            <button onClick={toggleFeature(pirate.hasHookHand)} style={{display: "block", margin: "16px 20px"}}>{pirate.hasHookHand ? "yes" : "no"}</button>
                        </div>
                    </div> 
                    <Link to="/">
                        <button className="blueButton">Crew Board</button>
                    </Link>
                </div>
            </div>
        </div>

    )
}

export default ShowOne;
