import React, {useState, useEffect} from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [pirate, setPirate]= useState({});
    const [name, setName] = useState("");
    const [position, setPosition] = useState("");
    const [hasPegLeg, setHasPegLeg] = useState("");
    const [hasEyePatch, setHasEyePatch] = useState("");
    const [hasHookHand, setHasHookHand] = useState("");
    const [url, setUrl] = useState("");
    const [catchPhrase, setCatchPhrase] = useState("");
    const [treasure, setTreasure] = useState("");
    const [errors, setErrors] = useState("");


    useEffect(() =>{
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setPirate(res.data);

                setHasPegLeg(res.data.hasPegLeg);
                setHasHookHand(res.data.hasHookHand);
                setHasEyePatch(res.data.hasEyePatch);
                setName(res.data.name);
                setPosition(res.data.position);
                setUrl(res.data.url);
                setCatchPhrase(res.data.catchPhrase);
                setTreasure(res.data.treasure);

            })
    },[])

    // const toggleEyePatch = () => {
    //     pirate.hasEyePatch= !pirate.hasEyePatch
    // }

    const myFunction = (e) => {
        e.preventDefault();
        console.log(`Peg Leg is ${pirate.hasPegLeg}`)
        console.log("my function")
        if (pirate.hasPegLeg==true){
            pirate.hasPegLeg = false;
        }
        else if (pirate.hasPegLeg == false){
            pirate.hasPegLeg = true;
        }
        setHasPegLeg= pirate.hasPegLeg;
        console.log(`Peg Leg is ${pirate.hasPegLeg}`)

        axios
        .put(`http://localhost:8000/api/pirates/${props.id}`, {

            name: name,
            position: position,
            hasPegLeg: hasPegLeg,
            hasEyePatch: hasEyePatch,
            hasHookHand: hasHookHand,
            url: url,
            catchPhrase: catchPhrase,
            treasure: treasure
        })
        .then((res) => {
            if(res.data.errors){
                setErrors(res.data.errors);
            } else {
                navigate(`/pirates/${res.data._id}`)
            }
        })
        .catch((err)=>console.log(err));
    }

    const myFunctionV2 = (e) => {
        e.preventDefault();
        console.log(`Peg Leg is ${pirate.hasEyePatch}`)
        console.log("my function")
        if (pirate.hasEyePatch==true){
            pirate.hasEyePatch = false;
        }
        else if (pirate.hasEyePatch == false){
            pirate.hasEyePatch = true;
        }
        console.log(`Peg Leg is ${pirate.hasEyePatch}`)

        axios
        .put(`http://localhost:8000/api/pirates/${props.id}`, {

            name: name,
            position: position,
            hasPegLeg: hasPegLeg,
            hasEyePatch: hasEyePatch,
            hasHookHand: hasHookHand,
            url: url,
            catchPhrase: catchPhrase,
            treasure: treasure
        })
        .then((res) => {
            if(res.data.errors){
                setErrors(res.data.errors);
            } else {
                navigate(`/pirates/${res.data._id}`)
            }
        })
        .catch((err)=>console.log(err));
    }

    const myFunctionV3 = (e) => {
        e.preventDefault();
        console.log(`Peg Leg is ${pirate.hasHookHand}`)
        console.log("my function")
        if (pirate.hasHookHand==true){
            pirate.hasHookHand = false;
        }
        else if (pirate.hasHookHand == false){
            pirate.hasHookHand = true;
        }
        console.log(`Peg Leg is ${pirate.hasHookHand}`)

        axios
        .put(`http://localhost:8000/api/pirates/${props.id}`, {

            name: name,
            position: position,
            hasPegLeg: hasPegLeg,
            hasEyePatch: hasEyePatch,
            hasHookHand: hasHookHand,
            url: url,
            catchPhrase: catchPhrase,
            treasure: treasure
        })
        .then((res) => {
            if(res.data.errors){
                setErrors(res.data.errors);
            } else {
                navigate(`/pirates/${res.data._id}`)
            }
        })
        .catch((err)=>console.log(err));
    }

    // const toggleFeature = () => {
    //     console.log("toggle feature called");
    //     console.log(`hookHand is ${pirate.hasHookHand}`)
    //     if (pirate.hasHookHand==true){
    //         pirate.hasHookHand = false;
    //     }
    //     else if (pirate.hasHookHand == false){
    //         pirate.hasHookHand = true;
    //     }
    //     console.log(`hookHand is ${pirate.hasHookHand}`)
    // }

    return (
        <div className="wrapper" style={{padding: "0px"}}>
            <h2 className="App-navbar">{pirate.name}</h2>
            <div>
                <div className="fiftyFiftySplit">
                    <img src={pirate.url} alt="Pirate Image" width="200" height="100" style={{margin:"10px"}}/>
                    <h2>"{pirate.catchPhrase}"</h2>
                </div>


                <div className="fiftyFiftySplit" style={{backgroundColor: "white"}}>
                    <h3>About</h3>
                    <p>Position: {pirate.position}</p>
                    <p>Treasures: {pirate.treasure}</p>
                    <div>
                        <div style={{display: "inline-block"}}>
                            <p>Peg Leg: {pirate.hasPegLeg ? "yes" : "no"}</p>
                            <p>Eye Patch: {pirate.hasEyePatch ? "yes" : "no"}</p>
                            <p>Hook Hand: {pirate.hasHookHand ? "yes" : "no"}</p>
                        </div>
                        <div style = {{display: "inline-block"}}>
                            <button onClick={(e) =>myFunction(e)}style={{display: "block", margin: "16px 20px"}} onChange={(e) =>setHasPegLeg(e.target.value)}>{pirate.hasPegLeg ? "yes" : "no"}</button>
                            <button  onClick={(e) =>myFunctionV2(e)}style={{display: "block", margin: "16px 20px"}} onChange={(e) =>setHasEyePatch(e.target.value)}>{pirate.hasEyePatch ? "yes" : "no"}</button>
                            <button  onClick={(e) =>myFunctionV3(e)}style={{display: "block", margin: "16px 20px"}} onChange={(e) =>setHasHookHand(e.target.value)}>{pirate.hasHookHand ? "yes" : "no"}</button>


                        </div>
                    </div> 
                    <Link to="/">
                        <button className="blueButton">Crew Board</button>
                    </Link>
                </div>
            </div>

        </div>

    )
}

export default ShowOne;
