import React, {useState, useEffect} from 'react';
import {Link} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [pirate, setPirate]= useState({});

    useEffect(() =>{
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setPirate(res.data);
            })
    },[])

    return (
        <div className="wrapper" style={{padding: "0px"}}>
            <h2 className="App-navbar">{pirate.name}</h2>

            <div className="fiftyFiftySplit">
            <img src={pirate.url} alt="Pirate Image" width="200" height="100" style={{margin:"10px"}}/>
            </div>
            <p>"{pirate.catchPhrase}"</p>

            <div className="fiftyFiftySplit">

                <p>Position: {pirate.position}</p>
                <p>Treasures: {pirate.treasure}</p> 
                <p>Peg Leg: {pirate.hasPegLeg ? "yes" : "no"}</p>
                <p>Eye Patch: {pirate.hasEyePatch ? "yes" : "no"}</p>
                <p>Hook Hand: {pirate.hasHookHand ? "yes" : "no"}</p>
                <Link to="/">
                    <button className="blueButton">Crew Board</button>
                </Link>
            </div>
            <p>Type: {pirate.type}</p>
            <p>Description: {pirate.description}</p>


            <Link to={"/pirates/"+pirate._id+"/edit"}>
                <button>Edit Pirate</button>
            </Link>
        </div>
    )
}

export default ShowOne;
