import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")
const New = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [isKing, setIsKing] = useState("");
    const [errors, setErrors] = useState("");

    const addPet = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/pets/", {
                name: name,
                type: type,
                description: description
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/pets/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }
    return (
        <div>
            <h2>New Pet</h2>
            <form onSubmit={addPet}>
                <div>
                    <label>Name</label>
                    {errors.name ? (
                        <span>{errors.name.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Type</label>
                    {errors.type ? (
                        <span>{errors.type.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="type"
                        value={type}
                        onChange={(e) => setType(e.target.value)}
                    />
                </div>
                <div>
                    <label>Description</label>
                    {errors.description ? (
                        <span>{errors.description.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>
                <div>
                    <label>Make the King</label>
                    {errors.description ? (
                        <span>{errors.description.message}</span>
                    ) : null
                    }
                    <input
                        type="radio"
                        name="captain"
                        value={isKing}
                        onChange={(e) => setIsKing(true)}
                    />
                </div>
                <button type="submit" onClick = {(e) => addPet(e)}>
                    Add Pet
                </button>
            </form>
        </div>
    )
}

export default New;
