import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'

console.log("step 1")

const New = (props) => {
    console.log("step 1")

    const [name, setName] = useState("");
    const [position, setPosition] = useState("");   
    const [url, setUrl] = useState("");  
    const [errors, setErrors] = useState("");

    const [late, setLate]= useState(false);
    const [dueDate, setDueDate]= useState("");
    console.log("set and get");

    const addProject = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/Projects/", {
                name: name,
                position: position,
                url: url,
                dueDate: dueDate,
                late: late,
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/Projects/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }


    console.log("step 1")
    return (
        <div >

            <div className= "App-navbar">
                <h2>Add Project</h2>
                <button type="submit" onClick = {(e) => navigate(`/`)} className="blueButton" style={{margin: "5px 5px 5px 100px"}}>
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addProject}>
                    <div className="fiftyFiftySplit"> 
                        <div>
                            <label style= {{display: "block"}}>Project Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label style= {{display: "block"}}>Image URL</label>
                                {errors.name ? (
                                    <span>{errors.url.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={(e) => setUrl(e.target.value)}
                                />
                        </div>
                        <label style= {{display: "block"}}># of Treasure Chests</label>

                        <div>
                            <label style= {{display: "block"}}>Position</label>
                                {errors.name ? (
                                    <span>{errors.position.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="Position"
                                    value={position}
                                    onChange={(e) => setPosition(e.target.value)}
                                />
                        </div>

                    </div>
                    <div className="fiftyFiftySplit">

                            <button type="submit" onClick = {(e) => addProject(e)} className="blueButton">
                                Add Project
                            </button>

                    </div>
                </form>
            </div>
        </div>
    )
}

export default New;
