    return (
        <div >
            <div className= "App-navbar">
                <h2>Add Project</h2>
                <button type="submit" onClick = {(e) => goHome(e)} className="blueButton" style={{margin: "5px 5px 5px 100px"}}>
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addProject}>
                    <div className="fiftyFiftySplit"> 

                        <div>
                            <label style= {{display: "block"}}>Project Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={ validateName }
                            />
                        </div>

                        <div>
                            <label style= {{display: "block"}}>Image URL</label>
                                {errors.url ? (
                                    <p>{errors.url.message}</p>
                                ) : null
                                }  
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={validateUrl}
                                />
                        </div>



                    </div>
                    <div className="fiftyFiftySplit">
                        <input
                                type="date"
                                name="dueDate"
                                onChange={(e) => setDueDate(e.target.value)}

                        />
                        <div>
                            <label style= {{display: "block"}}>Crew Position</label>
                            <select name="position" onChange={(e) => setPosition(e.target.value)}>
                                <option value="Captain">Captain</option>
                                <option value="First Mate">First Mate</option>
                                <option value="Quarter Master">Quarter Master</option>
                                <option value="Boatswain">Boatswain</option>
                                <option value="Powder Monkey">Powder Monkey</option>
                            </select>
                        </div>



                            <button type="submit" onClick = {(e) => addProject(e)} className="blueButton">
                                Add Project
                            </button>
                    </div> 
                </form>
            </div>
        </div>
    )

}

