import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")

console.log("load here?");
const New = (props) => {
    console.log("load here?");

    const [name, setName] = useState("");
    const [errors, setErrors] = useState({});
    const [url, setUrl] = useState("");
    // const [hasPegLeg, setHasPegLeg] = useState(true);
    // const [hasEyePatch, setHasEyePatch] = useState(true);
    // const [hasHookHand, setHasHookHand] = useState(true);
    const [position, setPosition] = useState("Powder Monkey");
    const [dueDate, setDueDate] = useState("");
    const [late, setLate] = useState(false);
    // const [treasure, setTreasure] = useState("0");
    // const [catchPhrase, setCatchPhrase] = useState("");
    const [imgUrlError, setImgUrlError] = useState("");
    const [nameError, setNameError] = useState("");

    console.log("load here?");



    const addProject = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/projects/", {
                name: name,
                // hasPegLeg: hasPegLeg,
                // hasEyePatch: hasEyePatch,
                // hasHookHand: hasHookHand,
                position: position,
                dueDate: dueDate,
                // treasure: treasure,
                url: url,
                late: late,
                // catchPhrase: catchPhrase,
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.error.errors){
                    setErrors(res.data.error.errors);
                } else {
                    navigate(`/Projects/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    const goHome = (e) => {
        navigate(`/`);
    }


//FRONT END VALIDATION OF FIELD
    // const validateUrl = (e) => {
    //     // set is asynchronous
    //     if (e.target.value.length < 3) {
    //         setImgUrlError("url must be atleast 3 characters long");
