import React, {useState, useEffect } from 'react';
import {Link } from '@reach/router';
import axios from 'axios';
import '../App.css';

let today = new Date().toLocaleDateString();




console.log(today);

const ShowAll = (props) => {
    const [allProjects, setAllProjects] = useState([]);
    const [backlog, setBacklog] = useState("");
    const [inProgress, setInProgress] = useState("");
    const [completed, setCompleted] = useState("");
    const [name, setName] = useState("");
    const [dueDate, setDueDate] = useState("");

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/Projects")
            .then((res) => {
                console.log(res.data);
                setAllProjects(res.data);
              })
            .catch(err => console.log(err));

    }, [])

    const deleteProject = (e, ProjectId) => {
        e.preventDefault();
        axios
            .delete(`http://localhost:8000/api/Projects/${ProjectId}`)
            .then((res) => {
                console.log(res.data);
                setAllProjects(allProjects.filter((Project) => Project._id !== ProjectId))
            })
            .catch((err) => console.log(err));
    }

    const backlogToInProgress = (e) => {
      e.preventDefault();

      axios
      .put(`http://localhost:8000/api/Projects/${props.id}`, {
          name: name,
          type: type,
          description: description,
          isKing: isKing
      })
      .then((res) => {
          if(res.data.errors) {
              setErrors(res.data.errors);
          } else {
              navigate(`/Projects/${res.data._id}`);
          }
      })
      .catch((err) => console.log(err));
}




    return (
      <div className="wrapper">
        <div className="App-navbar">

          <h2>Project Manager</h2>

        </div>

        <div >
          <h3>Backlog</h3>
          { allProjects.map((Project, i) => {
            return (

              <div> 

                {Project.backlog ? 

                <p key={ i } className="projectThumb">

                  <div style={{display: "inline-block"}}>

                      <h4>{ Project.name }</h4>
                      <p>Due Date: {Project.dueDate}</p>



                    <button className="redButton" onClick={(e) => deleteProject(e, Project._id)}>Walk the Plank</button>



                  </div>
                </p>

                : null }
              </div>   
            )
          })}
        </div>




        <div>
          <Link to="/Projects/new">
            <button className="blueButton">Add New Project</button>
          </Link>
        </div>

      </div>
    )
}

export default ShowAll;
