import React, {useState, useEffect} from 'react';
import {Link} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [pirate, setPirate]= useState({});
    const [hasPegLeg, setHasPegLeg] = useState("");
    const [hasEyePatch, setHasEyePatch] = useState("");
    const [hasHookHand, setHasHookHand] = useState("");


    useEffect(() =>{
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setPirate(res.data);
                setHasPegLeg(res.data.hasPegLeg);

            })
    },[])

    // const toggleEyePatch = () => {
    //     pirate.hasEyePatch= !pirate.hasEyePatch
    // }

    const myFunction = (e) => {
        e.preventDefault();
        console.log(`Peg Leg is ${pirate.hasPegLeg}`)
        console.log("my function")
        if (pirate.hasPegLeg==true){
            pirate.hasPegLeg = false;
        }
        else if (pirate.hasPegLeg == false){
            pirate.hasPegLeg = true;
        }
        console.log(`Peg Leg is ${pirate.hasPegLeg}`)

    }


    // const toggleFeature = () => {
    //     console.log("toggle feature called");
    //     console.log(`hookHand is ${pirate.hasHookHand}`)
    //     if (pirate.hasHookHand==true){
    //         pirate.hasHookHand = false;
    //     }
    //     else if (pirate.hasHookHand == false){
    //         pirate.hasHookHand = true;
    //     }
    //     console.log(`hookHand is ${pirate.hasHookHand}`)
    // }

    return (
        <div className="wrapper" style={{padding: "0px"}}>
            <h2 className="App-navbar">{pirate.name}</h2>
            <div>
                <div className="fiftyFiftySplit">
                    <img src={pirate.url} alt="Pirate Image" width="200" height="100" style={{margin:"10px"}}/>
                    <h2>"{pirate.catchPhrase}"</h2>
                </div>


                <div className="fiftyFiftySplit" style={{backgroundColor: "white"}}>
                    <h3>About</h3>
                    <p>Position: {pirate.position}</p>
                    <p>Treasures: {pirate.treasure}</p>
                    <div>
                        <div style={{display: "inline-block"}}>
                            <p>Peg Leg: {pirate.hasPegLeg ? "yes" : "no"}</p>
                            <p>Eye Patch: {pirate.hasEyePatch ? "yes" : "no"}</p>
                            <p>Hook Hand: {pirate.hasHookHand ? "yes" : "no"}</p>
                        </div>
                        <div style = {{display: "inline-block"}}>
                            <button id="demo" onClick={(e) =>myFunction(e)}style={{display: "block", margin: "16px 20px"}}>{pirate.hasPegLeg ? "yes" : "no"}</button>
                            <button  style={{display: "block", margin: "16px 20px"}}>{pirate.hasEyePatch ? "yes" : "no"}</button>
                            <button  style={{display: "block", margin: "16px 20px"}}>{pirate.hasHookHand ? "yes" : "no"}</button>


                        </div>
                    </div> 
                    <Link to="/">
                        <button className="blueButton">Crew Board</button>
                    </Link>
                </div>
            </div>

        </div>

    )
}

export default ShowOne;
