import React, {useEffect, useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router';

const Edit = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [isKing, setIsKing] = useState("");
    const [errors, setErrors] = useState("");

    useEffect(() => {
        axios
            .get(`http://localhost:8000/api/pirates/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setName(res.data.name);
                setType(res.data.type);
                setDescription(res.data.description);
                setIsKing(res.data.isKing);
            })
    },[])

    const addPirate = (e) => {
        e.preventDefault();
        axios
            .put(`http://localhost:8000/api/pirates/${props.id}`, {
                name: name,
                type: type,
                description: description,
                isKing: isKing
            })
            .then((res) => {
                if(res.data.errors) {
                    setErrors(res.data.errors);
                } else {
                    navigate(`/pirates/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    return(
        <div>
            <h2>Edit Pirate Listing</h2>
            <form onSubmit={addPirate}>
                <div>
                    <label>Name</label>
                    {errors.name ? (
                        <span>{errors.name.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="name"
                        value= {name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Type</label>
                    {errors.type ? (
                        <span>{errors.type.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="type"
                        value= {type}
                        onChange={(e) => setType(e.target.value)}
                    />
                </div>
                <div>
                    <label>Description</label>
                    {errors.description ? (
                        <span>{errors.description.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="description"
                        value= {description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>
                <div>
                    <label>Are they King?</label>
                    {errors.name ? (
                        <span>{errors.name.message}</span>
                    ) : null
                    }
                    <input
                        type="checkbox"
                        name="Are they King?"
                        checked= {isKing}
                        value= {isKing}
                        onChange={(e) => setIsKing(e.target.checked)}
                    />
                </div>
                <button type="submit">Update Pirate Listing</button>
            </form>
        </div>
    )
}

export default Edit;
