import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")
const New = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [isKing, setIsKing] = useState(false);
    const [errors, setErrors] = useState("");
    const [url, setUrl] = useState("");
    const [hasPegLeg, setHasPegLeg] = useState(true);
    const [hasEyePatch, setHasEyePatch] = useState(true);
    const [hasHookHand, setHasHookHand] = useState(true);

    const addPirate = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/pirates/", {
                name: name,
                type: type,
                description: description,
                isKing: isKing,
                hasPegLeg: hasPegLeg,
                hasEyePatch: hasEyePatch,
                hasHookHand: hasHookHand
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/pirates/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    const goHome = (e) => {
        axios
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/`);
                }
            })
    }
    return (
        <div >
            <div className= "App-navbar">
                <h2>Add Pirate</h2>
                <button type="submit" onClick = {(e) => addPirate(e)} className="blueButton">
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addPirate}>
                    <div className="fiftyFiftySplit">
                        <div>
                            <label>Pirate Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Image URL</label>
                                {errors.name ? (
                                    <span>{errors.url.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={(e) => setName(e.target.value)}
                                />
                        </div>
                    </div>
                    <div className="fiftyFiftySplit">
                        <div>
                            <label>Type</label>
                            {errors.type ? (
                                <span>{errors.type.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="type"
                                value={type}
                                onChange={(e) => setType(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Description</label>
                            {errors.description ? (
                                <span>{errors.description.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="description"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Make the King</label>
                            {errors.description ? (
                                <span>{errors.isKing.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="king"
                                checked={isKing}
                                onChange={(e) => setIsKing(e.target.checked)}
                            />
                            <button type="submit" onClick = {(e) => addPirate(e)} className="blueButton">
                                Add Pirate
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default New;
