    //         })
    //         .catch((err) => console.log(err));
    // }

    const backlogToInProgress = (e, id) => {
      const [secondName, setSecondName] = useState("");
      e.preventDefault();
      console.log(`${id}`);

      console.log("backlog button clicked");
      setBacklog(false);
      setInProgress(true);



      if(backlog===false){

        axios
        .put(`http://localhost:8000/api/Projects/${id}`, {
            name: name,
            dueDate: dueDate,
            backlog: false,
            inProgress: true,
            completed: completed,
        })
        .then((res) => {
            console.log(res);
            navigate('/');
        })
        .catch((err) => console.log(err));
      }

      // // console.log(`Backlog is ${Project.backlog}`)
      //   console.log("my backlog function")
      //   let updatedProject = Project;

      //   if (updatedProject.backlog===true){
      //       updatedProject.backlog = false;
