      //   console.log(`backlog is ${updatedProject.hasPegLeg} and in Progress is ${updatedProject.inProgress}`);
      //   axios
      //       .put(`http://localhost:8000/api/Projects/${props.id}`, updatedProject)
      //   .then((res) => {
      //           navigate(`/`)   
      //   })
      //   .catch((err)=>console.log(err));
    }




      // console.log("backlog button clicked")
      // setBacklog(false);
      // setInProgress(true);
      // console.log(Project)

      // if(backlog===false){

      //   axios
      //   .put(`http://localhost:8000/api/Projects/${props.id}`, {
      //       name: name,
      //       dueDate: dueDate,
      //       backlog: backlog,
      //       inProgress: inProgress,
      //       completed: completed,
      //   })
      //   .then((res) => {
      //       console.log(res);
      //       navigate('/');
      //   })
      //   .catch((err) => console.log(err));
      // }






    return (
      <div >
        <div>

          <h2>Project Manager</h2>

        </div>
        <div style={{width: "span"},{margin:"0px"}}>
          <h3 className="colTitle">Backlog</h3>
          <h3 className="colTitle">In Process</h3>
          <h3 className="colTitle">Complete</h3>

        </div>

        <div className="aThird">

          { allProjects.map((Project, i) => {
            return (
              <div>  
                {Project.backlog ? 
                <p key={ i } className="projectThumb">
                  <div style={{display: "inline-block"}}>

                      <h4>{ Project.name }</h4>
                      <p>Due Date: {Project.dueDate}</p>
                      {/* <p>{Project._id}</p> */}
                    <button 
                      className="redButton" 
                      onClick={(e) => backlogToInProgress(e, Project._id, Project.name,)}>
                        Start Project
                    </button>

                  </div>
                </p>

                : null }
              </div>   
            )
          })}
        </div>

        <div className="aThird">

          { allProjects.map((Project, i) => {
            return (
              <div>  
                {Project.inProgress ? 
                <p key={ i } className="projectThumb">
                  <div style={{display: "inline-block"}}>

                      <h4>{ Project.name }</h4>
                      <p>Due Date: {Project.dueDate}</p>
                      <p>{Project._id}</p>
                    <button 
                      className="redButton" 
                      onClick={backlogToInProgress}>
                        Start Project
                    </button>

                  </div>
                </p>

                : null }
              </div>   
            )
          })}
        </div>

        <div className="aThird">

          { allProjects.map((Project, i) => {
            return (
              <div>  
                {Project.completed ? 
                <p key={ i } className="projectThumb">
                  <div style={{display: "inline-block"}}>

                      <h4>{ Project.name }</h4>
                      <p>Due Date: {Project.dueDate}</p>
                      <p>{Project._id}</p>
                    <button 
                      className="redButton" 
                      onClick={backlogToInProgress}>
                        Start Project
                    </button>

                  </div>
                </p>

                : null }
              </div>   
            )
          })}
        </div>




        <div>
          <Link to="/Projects/new">
            <button className="blueButton" style={{width:"300px"}}>Add New Project</button>
          </Link>
        </div>

      </div>
    )
}

export default ShowAll;
