import React, {useState, useEffect } from 'react';
import {Link } from '@reach/router';
import axios from 'axios';
import '../App.css';



const ShowAll = (props) => {
    const [allPirates, setAllPirates] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/pirates")
            .then((res) => {
                console.log(res.data);
                setAllPirates(res.data);
              })
            .catch(err => console.log(err));

    }, [])

    const deletePirate = (e, pirateId) => {
        e.preventDefault();
        axios
            .delete(`http://localhost:8000/api/pirates/${pirateId}`)
            .then((res) => {
                console.log(res.data);
                setAllPirates(allPirates.filter((pirate) => pirate._id !== pirateId))
            })
            .catch((err) => console.log(err));
    }

    return (
      <div className="wrapper">
        <div className="App-navbar">

          <h2>Show All Pirates</h2>
          <Link to="/pirates/new">
            <button className="blueButton">Add Pirate</button>
          </Link>
        </div>

        <div >
          { allPirates.map((pirate, i) => {
            return (
              <p key={ i } className="pirateThumb">
                <img src={pirate.url} alt="Pirate Image" style={{width: "100px"}, {margin:"30px"}, {display: "inline-block"}}/>
                <div style={{display: "inline-block"}, {backgroundColor: "black"}}>
                  <Link to={`/pirates/${pirate._id}`} styel={{display: "span"}}>
                    { pirate.name }
                  </Link>
                  <Link to={`/pirates/${pirate._id}/edit`} >
                    <button className="blueButton">Edit</button>
                  </Link>
                  <button className="redButton" onClick={(e) => deletePirate(e, pirate._id)}>Delete</button>
                </div>
              </p>
            )
          })}
        </div>
      </div>
    )
}

export default ShowAll;
