import React, {useEffect, useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router';

const Edit = (props) => {
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [isCaptain, setIsCaptain] = useState("");
    const [errors, setErrors] = useState("");

    useEffect(() => {
        axios
            .get(`http://localhost:8000/api/pets/${props.id}`)
            .then((res) => {
                console.log(res.data);
                setName(res.data.name);
                setType(res.data.type);
                setDescription(res.data.description);
            })
    },[])

    const addPet = (e) => {
        e.preventDefault();
        axios
            .put("http://localhost:8000/api/pets/"+props.id, {
                name: name,
                type: type,
                description: description
            })
            .then((res) => {
                if(res.data.errors) {
                    setErrors(res.data.errors);
                } else {
                    navigate(`/pets/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    return(
        <div>
            <h2>Edit Pet Listing</h2>
            <form onSubmit={addPet}>
                <div>
                    <label>Name</label>
                    {errors.name ? (
                        <span>{errors.name.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="name"
                        value= {name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Type</label>
                    {errors.type ? (
                        <span>{errors.type.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="type"
                        value= {type}
                        onChange={(e) => setType(e.target.value)}
                    />
                </div>
                <div>
                    <label>Description</label>
                    {errors.description ? (
                        <span>{errors.description.message}</span>
                    ) : null
                    }
                    <input
                        type="text"
                        name="description"
                        value= {description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>
                <div>
                    <label>Are they Captain?</label>
                    {errors.name ? (
                        <span>{errors.name.message}</span>
                    ) : null
                    }
                    <input
                        type="radio"
                        name="Are they captain?"
                        value= {isCaptain}
                        onChange={(e) => setIsCaptain(e.target.value)}
                    />
                </div>
                <button type="submit">Update Pet Listing</button>
            </form>
        </div>
    )
}

export default Edit;
