import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")
const New = (props) => {
    const [name, setName] = useState("");
    const [errors, setErrors] = useState({});
    const [url, setUrl] = useState("");
    const [hasPegLeg, setHasPegLeg] = useState(true);
    const [hasEyePatch, setHasEyePatch] = useState(true);
    const [hasHookHand, setHasHookHand] = useState(true);
    const [position, setPosition] = useState("Powder Monkey");
    const [treasure, setTreasure] = useState("0");
    const [catchPhrase, setCatchPhrase] = useState("");
    const [imgUrlError, setImgUrlError] = useState("");

    const addPirate = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/pirates/", {
                name: name,
                hasPegLeg: hasPegLeg,
                hasEyePatch: hasEyePatch,
                hasHookHand: hasHookHand,
                position: position,
                treasure: treasure,
                url: url,
                catchPhrase: catchPhrase,
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.error.errors){
                    setErrors(res.data.error.errors);
                // } else {
                //     navigate(`/pirates/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    const goHome = (e) => {
        navigate(`/`);
    }

    const validateFirstName = (e) => {
        // set is asynchronous
        if (e.target.value.length < 3) {
            setErrors({ ...errors, name: { message: "name must be atleast 3 characters long"}});
        } else {
            setErrors({ ...errors, name: { message: ""}});
        }
        setName(e.target.value);  
    }

    const validateUrl = (e) => {
        // set is asynchronous
        if (e.target.value.length < 3) {
            setImgUrlError("URL must be atleast 3 characters long");
        } else {
            setImgUrlError("");
        }
        setUrl(e.target.value);  
    }

    return (
        <div >
            <div className= "App-navbar">
                <h2>Add Pirate</h2>
                <button type="submit" onClick = {(e) => goHome(e)} className="blueButton" style={{margin: "5px 5px 5px 100px"}}>
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addPirate}>
                    <div className="fiftyFiftySplit"> 
                        <div>
                            <label style= {{display: "block"}}>Pirate Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={ validateFirstName }
                            />
                        </div>
                        <div>
                            <label style= {{display: "block"}}>Image URL</label>
                                {errors.url ? (
                                    <p>{errors.url.message}</p>
                                ) : null
                                }
                                {imgUrlError !== "" ? (
                                    <p>{imgUrlError}</p>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={validateUrl}
                                />
                        </div>
                        <label style= {{display: "block"}}># of Treasure Chests</label>
                        <select name="position" onChange={(e) => setTreasure(e.target.value)}>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                        </select>
                        <div>
                            <label style= {{display: "block"}}>Pirate Catch Phrase</label>
                                {errors.catchPhrase ? (
                                    <span>{errors.catchPhrase.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="catchPhrase"
                                    value={catchPhrase}
                                    onChange={(e) => setCatchPhrase(e.target.value)}
                                />
                        </div>

                    </div>
                    <div className="fiftyFiftySplit">
                        <div>
                            <label style= {{display: "block"}}>Crew Position</label>
                            <select name="position" onChange={(e) => setPosition(e.target.value)}>
                                <option value="Captain">Captain</option>
                                <option value="First Mate">First Mate</option>
                                <option value="Quarter Master">Quarter Master</option>
                                <option value="Boatswain">Boatswain</option>
                                <option value="Powder Monkey">Powder Monkey</option>
                            </select>
                        </div>

                        <div>
                            {errors.hasPegLeg ? (
                                <span>{errors.hasPegLeg.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="pegLeg"
                                checked={hasPegLeg}
                                onChange={(e) => setHasPegLeg(e.target.checked)}
                            />
                            <label>Peg Leg</label>      
                        </div>
                        <div>
                            {errors.hasEyePatch ? (
                                <span>{errors.hasEyePatch.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="eyePatch"
                                checked={hasEyePatch}
                                onChange={(e) => setHasEyePatch(e.target.checked)}
                            />
                            <label>Eye Patch</label>      
                        </div>
                        <div>
                            {errors.hasHookHand ? (
                                <span>{errors.hasHookHand.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="hookHand"
                                checked={hasHookHand}
                                onChange={(e) => setHasHookHand(e.target.checked)}
                            />
                            <label>Hook Hand</label>      
                        </div>
                            <button type="submit" onClick = {(e) => addPirate(e)} className="blueButton">
                                Add Pirate
                            </button>

                    </div>
                </form>
            </div>
        </div>
    )
}

export default New;
