import React, {useState, useEffect } from 'react';
import {Link } from '@reach/router';
import axios from 'axios';
import '../App.css';



const ShowAll = (props) => {
    const [allPirates, setAllPirates] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/pirates")
            .then((res) => {
                console.log(res.data);
                setAllPirates(res.data);
              })
            .catch(err => console.log(err));

    }, [])

    const deletePirate = (e, pirateId) => {
        e.preventDefault();
        axios
            .delete(`http://localhost:8000/api/pirates/${pirateId}`)
            .then((res) => {
                console.log(res.data);
                setAllPirates(allPirates.filter((pirate) => pirate._id !== pirateId))
            })
            .catch((err) => console.log(err));
    }

    return (
      <div className="wrapper">
        <div className="App-navbar">

          <h2>Pirate Crew</h2>
          <Link to="/pirates/new">
            <button className="blueButton">Add Pirate</button>
          </Link>
        </div>

        <div >
          { allPirates.map((pirate, i) => {
            return (
              <p key={ i } className="pirateThumb">
                <img src={pirate.url} alt="Pirate Image" style={{width: "100px"}, {margin:"10px"}}/>
                <div style={{display: "inline-block"}}>

                    <h4>{ pirate.name }</h4>
                  <Link to={`/pirates/${pirate._id}`} >
                    <button className="blueButton">View Pirate</button>
                  </Link>
                  <button className="redButton" onClick={(e) => deletePirate(e, pirate._id)}>Walk the Plank</button>
                </div>
              </p>
            )
          })}
        </div>
      </div>
    )
}

export default ShowAll;
