import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")
const New = (props) => {
    const [Project, setProject]= useState({});
    const [name, setName] = useState("");
    const [position, setPosition] = useState("");   
    const [url, setUrl] = useState("");  
    const [errors, setErrors] = useState("");

    const [late, setLate]= useState("");
    const [dueDate, setDueDate]= useState("");

    const addProject = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/Projects/", {
                name: name,
                position: position,
                url: url,
                dueDate: dueDate,
                late: late,
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/Projects/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    const goHome = (e) => {
        axios
            .post("http://localhost:8000/api/Projects/", {
                    name: name,
                    hasPegLeg: hasPegLeg,
                    hasEyePatch: hasEyePatch,
                    hasHookHand: hasHookHand,
                    position: position,
                    treasure: treasure,
                    catchPhrase: catchPhrase,
                })
                .then((res) => {
                    console.log(res.data);
                    if(res.data.errors){
                        setErrors(res.data.errors);
                    } else {
                        navigate(`/`);
                    }
                })
                .catch((err) => console.log(err));
    }
    return (
        <div >
            <div className= "App-navbar">
                <h2>Add Project</h2>
                <button type="submit" onClick = {(e) => goHome(e)} className="blueButton" style={{margin: "5px 5px 5px 100px"}}>
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addProject}>
                    <div className="fiftyFiftySplit"> 
                        <div>
                            <label style= {{display: "block"}}>Project Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label style= {{display: "block"}}>Image URL</label>
                                {errors.name ? (
                                    <span>{errors.url.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={(e) => setUrl(e.target.value)}
                                />
                        </div>
                        <label style= {{display: "block"}}># of Treasure Chests</label>
                        <select name="position" onChange={(e) => setTreasure(e.target.value)}>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                        </select>
                        <div>
                            <label style= {{display: "block"}}>Project Catch Phrase</label>
                                {errors.name ? (
                                    <span>{errors.catchPhrase.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="catchPhrase"
                                    value={catchPhrase}
                                    onChange={(e) => setCatchPhrase(e.target.value)}
                                />
                        </div>

                    </div>
                    <div className="fiftyFiftySplit">
                        <div>
                            <label style= {{display: "block"}}>Crew Position</label>
                            <select name="position" onChange={(e) => setPosition(e.target.value)}>
                                <option value="Captain">Captain</option>
                                <option value="First Mate">First Mate</option>
                                <option value="Quarter Master">Quarter Master</option>
                                <option value="Boatswain">Boatswain</option>
                                <option value="Powder Monkey">Powder Monkey</option>
                            </select>
                        </div>

                        <div>
                            {errors.description ? (
                                <span>{errors.hasPegLeg.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="pegLeg"
                                checked={hasPegLeg}
                                onChange={(e) => setHasPegLeg(e.target.checked)}
                            />
                            <label>Peg Leg</label>      
                        </div>
                        <div>
                            {errors.description ? (
                                <span>{errors.hasEyePatch.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="eyePatch"
                                checked={hasEyePatch}
                                onChange={(e) => setHasEyePatch(e.target.checked)}
                            />
                            <label>Eye Patch</label>      
                        </div>
                        <div>
                            {errors.description ? (
                                <span>{errors.hasHookHand.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="hookHand"
                                checked={hasHookHand}
                                onChange={(e) => setHasHookHand(e.target.checked)}
                            />
                            <label>Hook Hand</label>      
                        </div>
                            <button type="submit" onClick = {(e) => addProject(e)} className="blueButton">
                                Add Project
                            </button>

                    </div>
                </form>
            </div>
        </div>
    )
}

export default New;
