import React, {useState, useEffect } from 'react';
import {Link } from '@reach/router';
import axios from 'axios';

const ShowAll = (props) => {
    const [allPets, setAllPets] = useState([]);

    useEffect(() => {
        axios
            .get("https://localhost:8000/api/pets")
            .then((res) => {
                console.log(res.data);
                setAllPets(res.data);
              })
            .catch(err => console.log(err));

    }, [])

    const deletePet = (e, petId) => {
        e.preventDefault();
        axios
            .delete(`https://localhost:8000/api/pets/${petId}`)
            .then((res) => {
                console.log(res.data);
                setAllPets(allPets.filter((pet) => pet._id !== petId))
            })
            .catch((err) => console.log(err));
    }

    return (
      <div>
        <h2>Show All Pets</h2>
        <Link to="/pets/new">
          <button>Add Pet</button>
        </Link>
        { allPets.map((pet, i) => {
          return (
            <p key={ i }>
              <Link to={`/pets/${pet._id}`} >
                { pet.title }
              </Link>
              <Link to={`/pets/${pet._id}/edit`} >
                <button className="blueButton">Edit</button>
              </Link>
              <button className="redButton" onClick={(e) => deletePet(e, pet._id)}>Delete</button>
            </p>
          )
        })}
      </div>
    )
}

export default ShowAll;
