import React, {useState} from 'react';
import axios from 'axios';
import {navigate} from '@reach/router'
// console.log("loaded new.js")
const New = (props) => {
    const [name, setName] = useState("");
    const [isKing, setIsKing] = useState(false);
    const [errors, setErrors] = useState("");
    const [url, setUrl] = useState("");
    const [hasPegLeg, setHasPegLeg] = useState(true);
    const [hasEyePatch, setHasEyePatch] = useState(true);
    const [hasHookHand, setHasHookHand] = useState(true);
    const [position, setPosition] = useState("Powder Monkey");
    const [treasure, setTreasure] = useState("0");
    const [catchPhrase, setCatchPhrase] = useState("");

    const addPirate = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8000/api/pirates/", {
                name: name,
                isKing: isKing,
                hasPegLeg: hasPegLeg,
                hasEyePatch: hasEyePatch,
                hasHookHand: hasHookHand,
                position: position,
                treasure: treasure,
                url: url,
                catchPhrase: catchPhrase,
            })
            .then((res) => {
                console.log(res.data);
                if(res.data.errors){
                    setErrors(res.data.errors);
                } else {
                    navigate(`/pirates/${res.data._id}`);
                }
            })
            .catch((err) => console.log(err));
    }

    const goHome = (e) => {
        axios
            .post("http://localhost:8000/api/pirates/", {
                    name: name,
                    isKing: isKing,
                    hasPegLeg: hasPegLeg,
                    hasEyePatch: hasEyePatch,
                    hasHookHand: hasHookHand,
                    position: position,
                    treasure: treasure,
                    catchPhrase: catchPhrase,
                })
                .then((res) => {
                    console.log(res.data);
                    if(res.data.errors){
                        setErrors(res.data.errors);
                    } else {
                        navigate(`/`);
                    }
                })
                .catch((err) => console.log(err));
    }
    return (
        <div >
            <div className= "App-navbar">
                <h2>Add Pirate</h2>
                <button type="submit" onClick = {(e) => goHome(e)} className="blueButton">
                    Crew Board
                </button>
            </div>
            <div className="wrapper">
                <form onSubmit={addPirate}>
                    <div className="fiftyFiftySplit"> 
                        <div>
                            <label style= {{display: "block"}}>Pirate Name</label>
                            {errors.name ? (
                                <span>{errors.name.message}</span>
                            ) : null
                            }
                            <input
                                type="text"
                                name="name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label style= {{display: "block"}}>Image URL</label>
                                {errors.name ? (
                                    <span>{errors.url.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="url"
                                    value={url}
                                    onChange={(e) => setUrl(e.target.value)}
                                />
                        </div>
                        <label style= {{display: "block"}}># of Treasure Chests</label>
                        <select name="position" onChange={(e) => setTreasure(e.target.value)}>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                        </select>
                        <div>
                            <label style= {{display: "block"}}>Pirate Catch Phrase</label>
                                {errors.name ? (
                                    <span>{errors.catchPhrase.message}</span>
                                ) : null
                                }
                                <input
                                    type="text"
                                    name="catchPhrase"
                                    value={catchPhrase}
                                    onChange={(e) => setCatchPhrase(e.target.value)}
                                />
                        </div>

                    </div>
                    <div className="fiftyFiftySplit">
                        <div>
                            <label style= {{display: "block"}}>Crew Position</label>
                            <select name="position" onChange={(e) => setPosition(e.target.value)}>
                                <option value="Captain">Captain</option>
                                <option value="First Mate">First Mate</option>
                                <option value="Quarter Master">Quarter Master</option>
                                <option value="Boatswain">Boatswain</option>
                                <option value="Powder Monkey">Powder Monkey</option>
                            </select>
                        </div>

                        <div>
                            <label>Make the King</label>
                            {errors.description ? (
                                <span>{errors.isKing.message}</span>
                            ) : null
                            }
                            <input
                                type="checkbox"
                                name="king"
                                checked={isKing}
                                onChange={(e) => setIsKing(e.target.checked)}
                            />
                            <button type="submit" onClick = {(e) => addPirate(e)} className="blueButton">
                                Add Pirate
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default New;
